import{_ as a}from"./chunks/schema-fonctionnement.CrT0GjP7.js";import{_ as r,c as t,o as i,ae as s}from"./chunks/framework.BR0IJ75L.js";const g=JSON.parse('{"title":"{Shimera} How Shimera Works","description":"","frontmatter":{},"headers":[],"relativePath":"en/library/fonctionnement_lib.md","filePath":"en/library/fonctionnement_lib.md"}'),o={name:"en/library/fonctionnement_lib.md"};function l(n,e,c,p,h,d){return i(),t("div",null,[...e[0]||(e[0]=[s('<h1 id="shimera-how-shimera-works" tabindex="-1"><strong>{Shimera} How Shimera Works</strong> <a class="header-anchor" href="#shimera-how-shimera-works" aria-label="Permalink to &quot;**{Shimera} How Shimera Works**&quot;">​</a></h1><h2 id="development-steps" tabindex="-1">Development Steps <a class="header-anchor" href="#development-steps" aria-label="Permalink to &quot;Development Steps&quot;">​</a></h2><ol><li><p><strong>Examples without OpenGL shaders</strong><br> We start by creating examples without using shaders, to validate the basic functionality.</p></li><li><p><strong>Direct addition of shaders</strong><br> Shaders are then integrated into the examples directly, <strong>without encapsulation or classes</strong>.</p></li><li><p><strong>Encapsulation of OpenGL calls</strong><br> OpenGL calls are encapsulated within the library, allowing examples to use only SFML.</p></li><li><p><strong>Encapsulation of SFML calls needed for shaders</strong><br> SFML calls used to invoke OpenGL shaders are also encapsulated.</p></li><li><p><strong>Simplified user usage</strong><br> The user only needs <strong>a single call to display a shader</strong>, regardless of the graphics backend.</p></li></ol><hr><h2 id="library-architecture" tabindex="-1">Library Architecture <a class="header-anchor" href="#library-architecture" aria-label="Permalink to &quot;Library Architecture&quot;">​</a></h2><ul><li>The <strong>OpenGL</strong> part is independent and works with all supported graphics libraries.</li><li><strong>Specific interfaces and abstractions</strong> add details for each target library. <ul><li>Example: SFML provides specific calls to invoke shaders.</li></ul></li><li>The goal is to provide a simple and unified interface for the user, hiding all the complexity of low-level calls.</li></ul><p><img src="'+a+'" alt="alt text"></p>',7)])])}const f=r(o,[["render",l]]);export{g as __pageData,f as default};
